    private void DisplayPIActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DisplayPIActionPerformed

        String userspath =  "C:\\Users\\Kristine L. Lopez\\Documents\\Github\\House-Cleaning-Booking-System\\HouseCleaningBookingSystem\\fd\\house-cleaning\\House_Cleaning_Service\\UserPersonalData.txt";

        try (BufferedReader reader = new BufferedReader(new FileReader(userspath))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Split the line into field name and value
                String[] fields = line.split(": ");
                // Set fields to corresponding labels
                if (fields.length == 2) {
                    switch (fields[0]) {
                        case "Full Name":
                        pi_name.setText(fields[1]);
                        break;
                        case "Username":
                        pi_username.setText(fields[1]);
                        break;
                        case "Address":
                        pi_address.setText(fields[1]);
                        break;
                        case "Email":
                        pi_email.setText(fields[1]);
                        break;
                        case "Age":
                        pi_age.setText(fields[1]);
                        break;
                        case "Phone Number":
                        pi_number.setText(fields[1]);
                        break;
                        case "Birthdate":
                        pi_birthdate.setText(fields[1]);
                        break;
                        case "Gender":
                        pi_gender.setText(fields[1]);
                        break;
                    }
                }
            }
        } catch (IOException ex) {
            ex.printStackTrace();
        }

    }//GEN-LAST:event_DisplayPIActionPerformed

